$(() => {
    let i=1;
    setInterval(()=>{
      i=Number(!i);
      $('body').find('.crsr').css("opacity",i)
    },400)
    const 
    
    randomNum = (min = 0, max = 4)=>{
        return (min + Math.floor(Math.random() * (max - min + 1)));
      },
    wait= (min = 1000, max = 4500) => new Promise(r => {
        if (max == null) {
          max = min;
          min = 0;
        }
        setTimeout(r, (min + Math.floor(Math.random() * (max - min + 1))))
      });
      const 
      getUserMsg = ()=>{
        return `<div class="w-full border-b border-black/10 dark:border-gray-900/50 text-gray-800 dark:text-gray-100 group dark:bg-gray-800">
        <div class="text-base gap-4 md:gap-6 m-auto md:max-w-2xl lg:max-w-2xl xl:max-w-3xl p-4 md:py-6 flex lg:px-0">
            <div class="w-[30px] flex flex-col relative items-end">
                <div style="background-color:#f0c2f6;" class="bg-[#fffff] rounded-sm text-white flex justify-center items-center relative tracking-widest h-8 w-8 text-xs">
                    <img src="data:image/png;base64," style="width: 27px;"/>   
                </div>
            </div>
            <div class="relative flex w-[calc(100%-50px)] md:flex-col lg:w-[calc(100%-115px)]">
                <div class="flex flex-grow flex-col gap-3">
                    <div class="min-h-[20px] flex flex-col items-start gap-4 whitespace-pre-wrap" id="user-text"></div>
                </div>
                <div class="text-gray-400 flex self-end lg:self-center justify-center mt-2 gap-4 lg:gap-1 lg:absolute lg:top-0 lg:translate-x-full lg:right-0 lg:mt-0 lg:pl-2 visible">
                    
                </div>
            </div>
        </div>
    </div>`;
      },
      getBotMsg = ()=>{
        return `<div class="w-full border-b border-black/10 dark:border-gray-900/50 text-gray-800 dark:text-gray-100 group bg-gray-50 dark:bg-[#444654]">
        <div class="text-base gap-4 md:gap-6 m-auto md:max-w-2xl lg:max-w-2xl xl:max-w-3xl p-4 md:py-6 flex lg:px-0">
            <div class="w-[30px] flex flex-col relative items-end">
                <div class="relative h-[30px] w-[30px] p-1 rounded-sm text-white flex items-center justify-center" style="background-color:#000;">
                    <svg  width="24" height="24" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg" xmlns:xlink="http://www.w3.org/1999/xlink">
                        <rect width="24" height="24" fill="url(#pattern0)"></rect>
                        <defs>
                            <pattern id="pattern0" patternContentUnits="objectBoundingBox" width="1" height="1">
                                <use xlink:href="#image0_102_53" transform="scale(0.01)"></use>
                            </pattern>
                            <image id="image0_102_53" width="100" height="100" xlink:href="data:image/png;base64,"></image>
                        </defs>
                    </svg>
                </div>
            </div>
            <div class="relative flex w-[calc(100%-50px)] md:flex-col lg:w-[calc(100%-115px)]">
                <div class="flex flex-grow flex-col gap-3">
                    <div class="min-h-[20px] flex flex-col items-start gap-4 whitespace-pre-wrap">
                        <img src="" id="gif-img" style="display:none"/>
                        <div class="markdown prose w-full break-words dark:prose-invert light"><p id="bot-text">I'm sorry, your question seems to be incomplete. Can you please provide more information so I can assist you?</p></div>
                    </div>
                </div>
                <div class="text-gray-400 flex self-end lg:self-center justify-center mt-2 gap-4 lg:gap-1 lg:absolute lg:top-0 lg:translate-x-full lg:right-0 lg:mt-0 lg:pl-2 visible"></div>
            </div>
        </div>
    </div>`
      },
      scrollBox = ()=>{
        let objDiv = document.getElementById("chat-box");
        objDiv.scrollTop = objDiv.scrollHeight;
      }
      
    
    const APP = {
        textArray : [`Compañeras y compañeros, ante la emergencia histórica que atraviesa nuestra querida patria, es preciso enfatizar el compromiso inquebrantable de nuestro movimiento peronista en la defensa irrestricta de los derechos y conquistas del pueblo trabajador. En este sentido, reivindicamos la lucha popular y colectiva como herramienta fundamental para el desarrollo integral y sostenible de nuestra sociedad, en consonancia con los valores de justicia social y solidaridad que siempre han caracterizado a nuestro proyecto nacional y popular. Por eso, ratificamos nuestro compromiso con el legado de nuestro líder eterno, el general Juan Domingo Perón, y con la continuidad de su obra transformadora, en pos de un futuro digno y soberano para todas y todos los argentinos y argentinas.`,
        `Compañeras y compañeros, en este momento crucial de la historia de nuestra nación, debemos reafirmar nuestra convicción irrenunciable de avanzar hacia una sociedad más justa, igualitaria y fraterna. Para ello, es necesario consolidar una coalición política amplia y plural, que permita la articulación de los diferentes sectores y actores sociales en una perspectiva de construcción colectiva del bien común. En este sentido, reiteramos nuestro compromiso con la defensa de los valores republicanos y democráticos, y con la profundización de un proyecto nacional y popular que tenga como horizonte el desarrollo integral y armónico de nuestra querida Argentina.`,
        `Compañeras y compañeros, en este momento histórico de la vida de nuestra nación, debemos enarbolar con fuerza las banderas del peronismo y del movimiento nacional y popular. Porque sabemos que solo con la unidad y la organización del pueblo trabajador podremos superar los desafíos y las adversidades que se nos presentan. Por eso, llamamos a todas y todos los argentinos y argentinas a sumarse a nuestra causa, a defender los derechos y conquistas del pueblo, y a construir juntos un futuro más justo, libre y soberano.`,
        `Compañeras y compañeros, en este momento de profunda crisis que atraviesa nuestra nación, es necesario reafirmar con firmeza nuestra convicción de que solo con la participación activa y comprometida del pueblo trabajador podremos superar los desafíos y las dificultades que se nos presentan. Por eso, hacemos un llamado a la unidad y la organización, y a la solidaridad y el compromiso con los más necesitados, en una perspectiva de construcción colectiva del bienestar y la felicidad del pueblo argentino.`,
        `Compañeras y compañeros, en este momento decisivo de la historia de nuestra patria, debemos reivindicar con fuerza el legado histórico del peronismo y del movimiento nacional y popular. Porque sabemos que solo a través de la defensa irrestricta de los derechos y conquistas del pueblo trabajador podremos construir una sociedad más justa, igualitaria y libre. En este sentido, ratificamos nuestro compromiso con la unidad y la organización del pueblo, y con la construcción colectiva de un futuro digno y soberano para todas y todos los argentinos y argentinas.`,
        `Compañeras y compañeros, en estos momentos en que el futuro de nuestra patria se encuentra en juego, es fundamental reivindicar la importancia histórica del movimiento peronista y su lucha por la justicia social y la igualdad. Desde nuestra posición política, hacemos un llamado a la unidad y al compromiso colectivo por el bienestar de todas y todos los argentinos y argentinas, y por la construcción de una sociedad más justa y solidaria. Solo así podremos superar los desafíos que se nos presentan y avanzar hacia un futuro digno y soberano.`,
        `Compañeras y compañeros, en esta coyuntura crítica que atraviesa nuestra nación, es imprescindible reivindicar el papel histórico del peronismo como motor de las transformaciones sociales y políticas que nuestra patria ha experimentado. En este sentido, ratificamos nuestro compromiso con la defensa de los derechos y conquistas del pueblo trabajador, y con la profundización de un proyecto nacional y popular que tenga como horizonte el desarrollo integral y armónico de nuestra querida Argentina.`,
        `Compañeras y compañeros, en este momento de incertidumbre y desafíos, es necesario hacer un llamado a la unidad y a la acción colectiva en defensa de los valores y principios que han guiado la lucha del movimiento peronista por la justicia social y la igualdad. Desde nuestra posición política, reafirmamos nuestro compromiso con el pueblo trabajador y con la construcción colectiva de una sociedad más justa y fraterna, que tenga como eje central el bienestar y la felicidad de todas y todos los argentinos y argentinas.`,
        `Compañeras y compañeros, en este momento histórico de la vida de nuestra nación, es imprescindible reivindicar la vigencia de las ideas y los valores que han guiado la lucha del movimiento peronista por la justicia social y la igualdad. Por eso, llamamos a todas y todos los argentinos y argentinas a sumarse a nuestra causa, a defender los derechos y conquistas del pueblo trabajador, y a construir juntos un futuro más justo, libre y soberano.`,
        `Compañeras y compañeros, en este momento de grandes desafíos y oportunidades para nuestra patria, reivindicamos con fuerza el legado histórico del peronismo como movimiento transformador y motor de las transformaciones sociales y políticas que nuestra nación ha experimentado. En este sentido, hacemos un llamado a la unidad y a la organización del pueblo trabajador, y a la construcción colectiva de un futuro digno y soberano para todas y todos los argentinos y argentinas.`
    ],
        sideBarPosition: {
            onHide: { left: "-20rem" },
            onShow: { left: "0rem" }
        },
        openMenu(){
            $('#sidebar-menu').show();
            $('#sidebar-menu .menubar').animate({ left: this.sideBarPosition.onShow.left });
        },closeMenu(){
            $('#sidebar-menu .menubar').animate({ left: this.sideBarPosition.onHide.left }).promise().then(()=>{
                $('#sidebar-menu').hide();
            });
        },
        async addWords(){
            scrollBox();
            $('body').find(".crsr").remove();
            let val = $('#text-area').val();
            console.log("val",val)
            if(val=="")return false;
            $('#text-area').prop("disabled",true)
            $('#main-page').hide();
            let uText = $(getUserMsg())
            uText.find('#user-text').html(val)
            $('#chat-box').append(uText).show();
            $('#text-area').val("")
            scrollBox();
            await wait(500,1000)
            let botMsgElem = $(getBotMsg());
            botMsgElem.find("#bot-text").html(`<span></span><span class="crsr"></span>`)
            botMsgElem.find('#gif-img').attr('src',`cfk.gif`).css('max-height','270px')
            $('#chat-box').append(botMsgElem)
            botMsgElem.find('#gif-img').on('load',function(){
              scrollBox();
            })
            scrollBox();
            let words = this.textArray[randomNum(0,this.textArray.length-1)];
            
            console.log(words)
            await wait(1000,5000);
            scrollBox();
            botMsgElem.find('#gif-img').show()
            for(let i of words||"".split("")){
                await wait(20,80);
                scrollBox();
                botMsgElem.find("#bot-text span").first().append(i)
            }
            await wait(200,500);
            $('#text-area').prop("disabled",false).focus()
    
            $('body').find(".crsr").remove();
          }
    }
    $('.new-cat').click(()=>{
      $('#chat-box').empty();
      $('#chat-box').hide();
      $('#main-page').show();
      APP.closeMenu()
    })
    $("#text-area").on('keyup', function (e) {
        if ((e.key === 'Enter' || e.keyCode === 13) && !e.ctrlKey ) {
            $('#chat-form').submit()
        }
    });
    $('#chat-form').on('submit',function(e){
        e.preventDefault();
        APP.addWords();
    })

    $('.close-menu').click(()=>{
        APP.closeMenu();
    })
    $('.open-menu').click(()=>{
        APP.openMenu();

    })
    
})